import { Component } from 'react';

class QuickSearchBox extends Component {

    render() {
        const  { imageSrc, heading, content } = this.props;
        return (
            <>
                <div className="col-12 col-md-6 col-lg-4">  
                    <div className="quickSearchBox">
                        <img src={imageSrc} width="160" height="160" alt="myimg" />
                        <div className="quickSearchDesc">
                            <h3>{heading}</h3>
                            <p>{content}</p>
                        </div>
                    </div>
                </div>
            </>
        );
    }
}

export default QuickSearchBox;
