import { Component } from 'react';

class Wallpaper extends Component {
    render() {
        const { cities } = this.props;
        return (
            <>
                <img src={require('../Assets/homepageimg.png').default} className="homeImage" alt="imager not found" />
                <div className="imageText">
                    <div className="logo">
                        e!
                    </div>
                    <div className="headerText">
                        Find the best restaurants, caf√©s, and bars
                    </div>
                </div>
                <div className="locationOptions row">
                    <div className="col-12 col-md-5 location-wrapper text-md-end text-center">
                        <select className="locationDropDown">
                            <option disabled selected>Select Location</option>
                            {
                                cities.map((item, index) => {
                                    return <option key={index} value={item.city_id}>{item.name}, {item.city}</option>
                                })
                            }
                        </select>
                    </div>
                    <div className="col-12 col-md-7 search-wrapper text-md-start text-center">
                        <input className="restaurantSearch" type="text" placeholder="Search the Restaurants" />
                    </div>  
                </div>
            </>
        );
    }
}

export default Wallpaper;
