import { Component } from 'react';
import axios from 'axios';

class Wallpaper extends Component {

    constructor() {
        super();
        this.state = {
            locations: [],
            locationsInCity: [],
            selectedCityName: '',
            mealtypeId: 0,
            mealTypeName: '',
            restaurantList: [],
            selectedLocation: 0,
            cuisines: [],
            hCost: undefined,
            lCost: undefined,
            sortOrder: 1,
            pageNo: 1,
            totalResults: 0,
            pageSize: 2,
            noOFPages: 0
        };
    }

    onOptionsChange(event) {
        const city_id = event.target.value;
        localStorage.setItem("city_id", city_id);
        axios.get('http://localhost:5402/api/getLocations')
            .then(result => {
                const locations = result.data.locations;
                const selectedCity = locations.find(city => city.city_id == city_id);
                const cityLocations = locations.filter(city => city.city_id == city_id);
                this.setState({
                    locations: result.data.locations,
                    selectedCityName: selectedCity.city,
                    locationsInCity: cityLocations,
                    selectedLocation: cityLocations[0].location_id
                });
                setTimeout(() => {
                    this.filterRestaurants();
                }, 0);
            })
            .catch(error => {
                console.log(error);
            });
    }

    render() {
        const { cities } = this.props;
        return (
            <>
                <img src={require('../Assets/homepageimg.png').default} className="homeImage" alt="imager not found" />
                <div className="imageText">
                    <div className="logo">
                        e!
                    </div>
                    <div className="headerText">
                        Find the best restaurants, caf√©s, and bars
                    </div>
                </div>
                <div className="locationOptions row">
                    <div className="col-12 col-md-5 location-wrapper text-md-end text-center">
                        <select className="locationDropDown" onChange={(event) => this.onOptionsChange(event)}>
                            <option disabled selected>Select Location</option>
                            {
                                cities.map((item, index) => {
                                    return <option key={index} value={item.city_id}>{item.name}, {item.city}</option>
                                })
                            }
                        </select>
                    </div>
                    <div className="col-12 col-md-7 search-wrapper text-md-start text-center">
                        <input className="restaurantSearch" type="text" placeholder="Search the Restaurants" />
                    </div>  
                </div>
            </>
        );
    }
}

export default Wallpaper;
