import React, { Component } from 'react';
import axios from 'axios';
import queryString from 'query-string';
import { withRouter } from 'react-router-dom';

import '../Styles/filter.css';

class Filter extends Component {

    constructor() {
        super();
        this.state = {
            locations: [],
            locationsInCity: [],
            selectedCityName: '',
            mealtypeId: 0,
            mealTypeName: '',
            restaurantList: [],
        };
    }
        

        componentDidMount() {

            const qs = queryString.parse(this.props.location.search);
            const  { mealtype, mealTypeName } = qs;
            this.setState({
                mealtypeId: mealtype,
                mealTypeName: mealTypeName,
            });

            const city_id = localStorage.getItem("city_id");
            axios.get('http://localhost:5402/api/getLocations')
            .then(result => {
                const locations = result.data.locations;
                const selectedCityName = locations.find(city => city.city_id == city_id);
                const cityLocations = locations.filter(city => city.city_id == city_id);
                this.setState({
                    locations: result.data.locations,
                    selectedCityName: selectedCityName.city,
                    locationsInCity: cityLocations,
                });
                this.filterRestaurants();
            })
            .catch(error => {
                console.log(error);
            });
    }

    filterRestaurants(){
        const { mealtypeId} = this.state;

        const req = {
            mealtype: mealtypeId,
        }

        axios({
            method: 'POST',
            url: 'http://localhost:5402/api/filterRestaurants',
            headers: { 'Content-Type': 'application/json' },
            data: req
        }).then(result => {
            this.setState({
                restaurantList: result.data.restaurants,
            });

        }).catch(error => {
            console.log(error);
        });
    }

    render() {
        const { locations, selectedCityName, locationsInCity, mealTypeName, restaurantList} = this.state;
        return (
            <>
                <div className="container">
                    <div className="row heading">
                       {mealTypeName} Places in {selectedCityName}
                    </div>
                    <div className="row">
                        <div className="col-3 filterSection">
                            <div className="sectionHeading">Filters</div>
                            <div className="sectionSubHeading">Select Location</div>
                            <select className="locationSelection">
                            {
                                    locationsInCity.map((item, index) => {
                                        return <option key={index} value={item.location_id}>{ item.name }</option>
                                    })
                                }
                            </select>
                            <div className="sectionSubHeading">Cuisine</div>
                            <div className="cuisineSelection"><input type="checkbox" /> North Indian</div>
                            <div className="cuisineSelection"><input type="checkbox" /> South Indian</div>
                            <div className="cuisineSelection"><input type="checkbox" /> Chinese</div>
                            <div className="cuisineSelection"><input type="checkbox" /> Fast Food</div>
                            <div className="cuisineSelection"><input type="checkbox" /> Street Food</div>
                            <div className="sectionSubHeading">Cost for two</div>
                            <div className="cuisineSelection"><input type="radio" name="cost" /> Less than &#8377; 500 </div>
                            <div className="cuisineSelection"><input type="radio" name="cost" /> &#8377; 500 to &#8377; 1000 </div>
                            <div className="cuisineSelection"><input type="radio" name="cost" /> &#8377; 1000 to &#8377; 1500 </div>
                            <div className="cuisineSelection"><input type="radio" name="cost" /> &#8377; 1500 to &#8377; 2000 </div>
                            <div className="cuisineSelection"><input type="radio" name="cost" /> &#8377; 2000+ </div>
                            <div className="sectionSubHeading">Sort</div>
                            <div className="cuisineSelection"><input type="radio" name="sort" /> Price low to high </div>
                            <div className="cuisineSelection"><input type="radio" name="sort" /> Price high to low </div>
                        </div>
                        <div className="col-8 resultSection">
                            {
                                restaurantList.map((item, index) => {
                                    return <div className="row resultBox">
                                                <div className="topSection row">
                                                    <div className="col-3">
                                                        <img className="resultImage" src={require('../Assets/breakfast.jpg').default} alt="myimage" />
                                                    </div>
                                                    <div className="col-9">
                                                        <div className="resultHeader">{item.name}</div>
                                                        <div className="resultSubHeader">{item.locality}</div>
                                                        <div className="resultDescription">{item.city}</div>
                                                    </div>
                                                </div>
                                                <hr />
                                                <div className="bottomSection row">
                                                    <div className="col-3">
                                                        <div className="resultdetils">
                                                            CUISINES:
                                                        </div>
                                                        <div className="resultdetils">
                                                            COST FOR TWO:
                                                        </div>
                                                    </div>
                                                    <div className="col-9">
                                                        <div className="resultdetilsOptions">
                                                            {
                                                                item.cuisine.map((c, i) => {
                                                                    return `${c.name}, `
                                                                })
                                                            }
                                                        </div>
